CREATE TABLE IF NOT EXISTS clipboardmanager (
"id" INTEGER PRIMARY KEY AUTOINCREMENT, 
"date" TEXT DEFAULT (date('now','localtime')),
"time" TEXT DEFAULT (time('Now', 'localtime')),
"clip" TEXT
);

CREATE TABLE IF NOT EXISTS savedmanager (
"id" INTEGER PRIMARY KEY AUTOINCREMENT, 
"date" TEXT DEFAULT (date('now','localtime')),
"time" TEXT DEFAULT (time('Now', 'localtime')),
"clip" TEXT
);

DELETE FROM clipboardmanager
WHERE EXISTS (
SELECT 1 FROM clipboardmanager t1
WHERE clipboardmanager.clip = t1.clip
AND clipboardmanager.id < t1.id
);

SELECT SUBSTR (clip,1,100)
FROM savedmanager
ORDER BY id DESC;

SELECT SUBSTR (clip,1,100)
FROM clipboardmanager
ORDER BY id DESC;

DELETE FROM savedmanager
WHERE EXISTS (
SELECT 1 FROM savedmanager t1
WHERE savedmanager.clip = t1.clip
AND savedmanager.id < t1.id
);

DROP TRIGGER limit_cbm_entries;
CREATE TRIGGER IF NOT EXISTS limit_cbm_entries
AFTER INSERT ON clipboardmanager
BEGIN 
DELETE FROM clipboardmanager
WHERE id IN 
(SELECT id FROM clipboardmanager LIMIT 1)
AND (SELECT COUNT(*) FROM clipboardmanager)=%limit;
END;
